								 {

								 }								 


					});

	  function querySearch2 (query) {		

		c.data.searchStr = query;	
		deferred =$q.defer();
		c.server.update().then(function(){			
			deferred.resolve(c.data.accounts);
		}, function myError( ){
			console.log("Query Search 2 Error: ");
		});		  
