			q: data.q,
			window_size: data.window_size || 10,

			show_breadcrumbs: true,
			hide_header: false,
			show_keywords: true,
			show_new: false,
			show_title: true,
			enable_filter:true,
			short_description:"Cash Machines"
		};


		data.dataTable2 = $sp.getWidget('tsp-data-table-temp', table2Params);
	}

})();
